find_hash <- function(secret, start = 1, difficulty) {
  cpt <- start

  zeros <- paste0(rep("0", difficulty), collapse = "")

  repeat {
    # if (cpt %% 10000 == 0) print(cpt)
    hash <- openssl::md5(paste0(secret, cpt))

    if (all(str_sub(hash, 1, difficulty) == zeros)) {
      break
    }

    cpt <- cpt + 1
  }

  return(cpt)
}

secret <- "iwrupvqb"



